mapper.select:
select DISTINCT a.*, b.di_name as DEVNAME,c.node_id as DEVORGID,c.node_name as DEVORGNAME,'报警' ALARM_TYPE from iot_realalarm a,iot_deviceinfo b,iot_orgtreenode c,iot_devicerole d where a.device_id = b.di_serialnum and b.di_nodeid = c.node_id and d.devrole_devnum = a.device_id and d.devrole_roleid IN
#{item}
order by a.alarm_code desc

select DISTINCT a.*, b.di_name as DEVNAME,c.node_id as DEVORGID,c.node_name as DEVORGNAME,'报警' ALARM_TYPE from IOT_HISALARM a,iot_deviceinfo b,iot_orgtreenode c,iot_devicerole d where a.device_id = b.di_serialnum and b.di_nodeid = c.node_id and d.devrole_devnum = a.device_id AND a.hisalarm_date BETWEEN to_date(#{sStartDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') AND to_date(#{sEndDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') and d.devrole_roleid IN
#{item}
order by a.hisalarm_date desc

select m.*,b.di_name as DEVNAME,c.node_id as DEVORGID,c.node_name as DEVORGNAME,'报警' ALARM_TYPE from (select count(a.hisalarm_id) qty ,a.device_id from ( select t.hisalarm_id hisalarm_id,t.device_id device_id, t.hisalarm_describe thisalarm , lead(t.hisalarm_describe, 1, null) over (partition by t.device_id order by t.device_id) nextalarm, t.hisalarm_date hisalarm_date from IOT_HISALARM t where hisalarm_date between to_date(#{sStartDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') and to_date(#{sEndDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') order by t.device_id,t.hisalarm_date asc )a where a.thisalarm !=a.nextalarm and nextalarm is not null and thisalarm!='正常' group by a.device_id order by qty desc) m,iot_deviceinfo b,iot_orgtreenode c,iot_devicerole d where m.device_id = b.di_serialnum and b.di_nodeid = c.node_id and d.devrole_devnum = m.device_id and d.devrole_roleid in
#{item,jdbcType=VARCHAR}
and Rownum < #{topN,jdbcType=INTEGER} order by m.qty desc
