mapper.select:
select DISTINCT a.*, b.di_name as DEVNAME,c.node_id as DEVORGID,c.node_name as DEVORGNAME,'错误' ALARM_TYPE from iot_realerror a,iot_deviceinfo b,iot_orgtreenode c,iot_devicerole d where a.drerror_devnum = b.di_serialnum and b.di_nodeid = c.node_id and d.devrole_devnum = a.drerror_devnum and d.devrole_roleid in
#{item}
order by a.drerror_receivetime desc

select DISTINCT a.*, b.di_name as DEVNAME,c.node_id as DEVORGID,c.node_name as DEVORGNAME,'错误' ALARM_TYPE from iot_hiserror a,iot_deviceinfo b,iot_orgtreenode c,iot_devicerole d where a.dherror_devnum = b.di_serialnum and b.di_nodeid = c.node_id and d.devrole_devnum = a.dherror_devnum AND a.dherror_receivetime BETWEEN to_date(#{sStartDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') AND to_date(#{sEndDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') and d.devrole_roleid in
#{item}
order by a.dherror_receivetime desc
