mapper.select:
select DISTINCT a.*, b.di_name as DEVNAME,c.node_id as DEVORGID,c.node_name as DEVORGNAME,'离线' ALARM_TYPE from iot_realoffline a,iot_deviceinfo b,iot_orgtreenode c,iot_devicerole d where a.droffline_devnum = b.di_serialnum and b.di_nodeid = c.node_id and d.devrole_devnum = a.droffline_devnum and d.devrole_roleid in
#{item}
order by a.droffline_devreceivetime desc

select DISTINCT a.*, b.di_name as DEVNAME,c.node_id as DEVORGID,c.node_name as DEVORGNAME,'离线' ALARM_TYPE from iot_hisoffline a,iot_deviceinfo b,iot_orgtreenode c,iot_devicerole d where a.dhoffline_devnum = b.di_serialnum and b.di_nodeid = c.node_id and d.devrole_devnum = a.dhoffline_devnum AND a.dhoffline_devreceivetime BETWEEN to_date(#{sStartDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') AND to_date(#{sEndDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss') and d.devrole_roleid =in
#{item}
order by a.dhoffline_devreceivetime desc
