mapper.insert:
INSERT INTO iot_userrole(USERROLE_USERID,USERROLE_USERNAME,USERROLE_ROLEID) (
SELECT #{item.userId} as USERROLE_USERID, #{item.userName} as USERROLE_USERNAME, #{item.roleId} as USERROLE_ROLEID FROM DUAL
)

mapper.delete:
DELETE from iot_userrole
WHERE USERROLE_USERID = #{userId}
AND USERROLE_ROLEID = #{roleId}
